mapper.select:
SELECT uid, email
FROM user_t
WHERE email = #{email}

SELECT uid, email, uname, auth, major, uimg, cid
FROM user_t
WHERE uid = #{uid}

mapper.update:
UPDATE user_t
SET pw = sha1(#{pw}), major = #{major}, uname = #{uname}
WHERE uid = #{uid}

mapper.select:
SELECT s.sid, s.tname
FROM myStack_t AS ms JOIN stack_t AS s ON ms.sid = s.sid
WHERE ms.uid = #{uid}

mapper.update:
UPDATE user_t
SET auth = #{auth}
WHERE uid = #{uid}
